/* Procedure structure for procedure `uppercase` */

/*!50003 DROP PROCEDURE IF EXISTS  `uppercase` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `uppercase`(IN dbname VARCHAR(200))
BEGIN 
 
DECLARE done INT DEFAULT 0; 
 
DECLARE oldname VARCHAR(200); 
 
DECLARE cur CURSOR FOR SELECT table_name FROM information_schema.TABLES WHERE table_schema = dbname; 
 
DECLARE CONTINUE HANDLER FOR NOT FOUND SET done = 1; 
 
OPEN cur; 
 
REPEAT 
 
FETCH cur INTO oldname; 
 
SET @newname = UPPER(oldname); 
 
#IF newname equals to oldname, do nothing; 
#select 'a' <> 'A'; -> 0 
#select 'a' <> BINARY 'A'; -> 1 
SET @isNotSame = @newname <> BINARY oldname; 
 
IF NOT done && @isNotSame THEN 
 
SET @SQL = CONCAT('rename table ',oldname,' to ',@newname); 
 
PREPARE tmpstmt FROM @SQL; 
 
EXECUTE tmpstmt; 
 
DEALLOCATE PREPARE tmpstmt; 
 
END IF; 
 
UNTIL done END REPEAT; 
 
CLOSE cur; 
 
END */$$
DELIMITER ;

